    def fetch_orders_by_status(self, status, symbol=None, since=None, limit=None, params={}):
        if symbol is None:
            raise ArgumentsRequired(self.id + ' fetchOrdersByStatus() requires a symbol argument')
        self.load_markets()
        market = self.market(symbol)
        marketType, query = self.handle_market_type_and_params('fetchOrdersByStatus', market, params)
        request = {}
        if market['spot']:
            request['symbol'] = market['id']
            request['offset'] = 1  # max offset * limit < 500
            request['limit'] = 100  # max limit is 100
            #  1 = Order failure
            #  2 = Placing order
            #  3 = Order failure, Freeze failure
            #  4 = Order success, Pending for fulfilment
            #  5 = Partially filled
            #  6 = Fully filled
            #  7 = Canceling
            #  8 = Canceled
            #  9 = Outstanding(4 and 5)
            # 10 = 6 and 8
            if status == 'open':
                request['status'] = 9
            elif status == 'closed':
                request['status'] = 6
            elif status == 'canceled':
                request['status'] = 8
            else:
                request['status'] = status
        elif market['swap'] or market['future']:
            request['contractID'] = market['id']
            # request['offset'] = 1
            if limit is not None:
                request['size'] = limit  # max 60
            # 0 = All
            # 1 = Submitting
            # 2 = Commissioned
            # 3 = 1 and 2
            # 4 = Completed
            if status == 'open':
                request['status'] = 3
            elif status == 'closed':
                request['status'] = 4
            else:
                request['status'] = status
        method = self.get_supported_mapping(marketType, {
            'spot': 'privateSpotGetOrders',
            'swap': 'privateContractGetUserOrders',
            'future': 'privateContractGetUserOrders',
        })
        response = getattr(self, method)(self.extend(request, query))
        #
        # spot
        #
        #     {
        #         "message":"OK",
        #         "code":1000,
        #         "trace":"70e7d427-7436-4fb8-8cdd-97e1f5eadbe9",
        #         "data":{
        #             "current_page":1,
        #             "orders":[
        #                 {
        #                     "order_id":2147601241,
        #                     "symbol":"BTC_USDT",
        #                     "create_time":1591099963000,
        #                     "side":"sell",
